    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String texta = jTextField1.getText();
        int a = Integer.parseInt(texta);
        listaEnlazada lista = new listaEnlazada();
        if(a > 0 && a < 10){
            lista.agregarPrimero(a);
            
            String m, n;
            
            m = String.valueOf(lista.obtener(0));
            n = String.valueOf(lista.obtener(-1));
            
            int intN = Integer.parseInt(n);
            int intM = Integer.parseInt(m);
            
            int[][] matriz = metodos3.generarMatriz(intN);
            String texto = "";
            
            for (int i = 0; i < matriz.length; i++) {
                for (int j = 0; j < matriz.length; j++) {
                    texto += String.format("%5d   ", matriz[i][j]);
                }
                texto += String.format("%n");
            }
            
            int num = intN * intM;
            metodos3.fibonacci(num);
            
            String suma = "";
            suma = metodos3.sumaElementos(intN, intM);
            
            int primos = 0;
            primos = metodos3.cantidadPrimo(intN);
            
            jTextField4.setText(m);
            jTextField5.setText(n);
            jTextArea2.setText(texto);
            jTextArea3.setText(String.valueOf(num));
            jTextField2.setText(suma);
            jTextField3.setText(String.valueOf(primos));
            
        }
    }//GEN-LAST:event_jButton1ActionPerformed

